@Component({
  selector: 'app-shorted',
  templateUrl: './shorted.component.html',
  styleUrls: ['./shorted.component.css']
})
export class ShortedComponent {
}


<div style="background-color:whitesmoke; height:200px">
  <div class="main" style="margin-left:400px; margin-top:150px;">
    <h4>Your Lengthy URL has been shorted</h4>
    <br>
    <div class="search-box">
      <input class="sb1" type="text" placeholder="" id="searchbox" style="width:500px; height:60px; border-radius:10px">
      <button style="width: 60px; height: 60px; margin-right: 10px;scroll-margin-bottom:200px; background-color: cornflowerblue; border-radius: 10px">Copy URL</button>
    </div>
  </div>
</div>
