import { Component } from '@angular/core';

@Component({
  selector: 'app-Login',
  templateUrl: './Login.component.html',
  styleUrls: ['./Login.component.css']
})
export class LoginComponent {
}

<body>

  <div class="loginbox">
    <h1>Login Here</h1>

    <form name="login_form">
      <p> Username</p>
      <input type="text" name="" placeholder="Enter Username" id="username">
      <p>E-mail</p>
      <input type="text" name="" placeholder="Enter Email Id" id="email">
      <p> Password</p>
      <input type="password" name="" id="pass" placeholder="Enter Password"><br>
      <input type="submit" name="" value="Login" onclick="formvalid()">
      <a href="#">Forgoten Password?</a> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      <a href="#">Don't have an acoount?</a>
    </form>
  </div>
   
</body>
