import { Component } from '@angular/core';

@Component({
  selector: 'app-password',
  templateUrl: './password.component.html',
  styleUrls: ['./password.component.css']
})
export class passwordComponent {
}
 <body>

  <div class="loginbox">
    <h1>Reset Your Password</h1>

    <form name="login_form">
      <p> Username</p>
      <input type="text" name="" placeholder="Enter Username" id="username">
      <p>E-mail</p>
      <input type="text" name="" placeholder="Enter Email Id" id="email">
      <p>Create new password</p>
      <input type="password" name="" id="pass" placeholder="Enter New Password"><br>
      <input type="submit" name="" value="Login" onclick="formvalid()">
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      <a [routerLink]="['/signup']">Don't have an acoount?</a>
    </form>
  </div>

</body>
