      <p> Username</p>
      <input type="text" name="" placeholder="Enter Username" id="username">
      <p>E-mail</p>
      <input type="text" name="" placeholder="Enter Email Id" id="email">
      <p> Password</p>
      <input type="password" name="" id="pass" placeholder="Enter Password"><br>
      <input type="submit" name="" value="Login" onclick="formvalid()">
